@app.route('/ask', methods=['POST'])
def ask_question():
    data = request.json  # Get the JSON data from the request
    user_question = data.get('question')  # Extract the question from the data
    answer = qa.get(user_question, "I'm sorry, I don't know the answer to that question.")  # Get the answer
    return jsonify({ 'answer': answer})  # Return the response as JSON

